import React from "react";
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import { useState } from 'react';
import axios from 'axios';
import { CircularProgress, Divider } from "@mui/material";



export function MarketGPT() {
  const [company, setCompany] = useState('');
  const [loading, setLoading] = useState(false);

  const [competitor, setCompetitor] = useState( {
    "test1": "sample",
    "test2": "sample2"
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);

    axios.post('http://localhost:8000/market/', { company })
      .then((response) => {
        console.log('1st then: ', response.data);
        setCompetitor(response.data);

        setLoading(false);
      })
      .catch((error) => {
        setCompany('An error occurred while creating the item.');
        console.error(error);
      });
  };
  return (
    <div style={{display: "flex"}}>
      <h1>Market Competitor Analysis</h1>
      <TextField id="outlined-basic" value = {company}  placeholder="Enter Company Name" label="Outlined" variant="outlined" onChange={(e) => setCompany(e.target.value)}/>
      <div>
        {
          loading ?
          <CircularProgress color="secondary" /> :
          <Button variant="contained" onClick={handleSubmit}>Submit</Button>
        }
      </div>
      <Divider />
      <div >
        {Object.keys(competitor).map((key, index) => (
          <div key={key}>
            <h3>{key}</h3> : <div> {competitor[key]} </div>
          </div>
        ))}
      </div>
    </div>
  );
}
