import React from "react";
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import { useState } from 'react';
import axios from 'axios';
import { CircularProgress, Container, Divider } from "@mui/material";



export function MarketGPT() {
  const [company, setCompany] = useState('');
  const [loading, setLoading] = useState(false);

  const [competitor, setCompetitor] = useState( {
    "market": "Business Software Solutions",
    "competitors": [
        {
            "company": "Microsoft Dynamics 365",
            "market_cap": "1.588T"
        },
        {
            "company": "Salesforce",
            "market_cap": "184.094B"
        },
        {
            "company": "Oracle",
            "market_cap": "182.547B"
        },
        {
            "company": "SAP",
            "market_cap": "137.941B"
        }
    ]
});

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);

    axios.post('http://localhost:8000/market/', { company })
      .then((response) => {
        console.log('1st then: ', response.data);
        setCompetitor(response.data);

        setLoading(false);
      })
      .catch((error) => {
        setCompany('An error occurred while creating the item.');
        console.error(error);
      });
  };
  return (
    <Container>
      <div style={{display: "flex", justifyContent: "center"}}>

        <div style={{display: "flex", flexDirection: "column", width: "50%"}}>
            <h1>Market Competitor Analysis</h1>

            <div style={{display: "flex", flexDirection: "column", alignItems: "center"}}>
              <TextField 
                id="outlined-basic" 
                value = {company}  
                placeholder="Enter Company Name" 
                label="Outlined" 
                variant="outlined" 
                onChange={(e) => setCompany(e.target.value)}
                style={{width: "100%"}}
                />

              <div style={{height: "100%"}}>
                {
                  loading ?
                  <CircularProgress 
                    color="secondary"
                    style={{margin: "20px"}}
                    /> :
                  <Button 
                    variant="contained" 
                    onClick={handleSubmit}
                    style={{margin: "20px"}}
                    >
                      Submit
                  </Button>
                }
              </div>
          </div>
          <Divider />
          <ResponseComponent response={competitor} />
          {/* <div >
            {Object.keys(competitor).map((key, index) => (
              <div key={key}>
                <h3>{key}</h3> : <div> {competitor[key]} </div>
              </div>
            ))}
          </div> */}
        </div>
      </div>
    </Container>
  );
}

function ResponseComponent({ response }){

  return (
    <div>
      <h3> market: </h3> <h1> {response.market}</h1>
    </div>
  );
}
