import React from "react";
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import { useState } from 'react';
import axios from 'axios';
import { CircularProgress, Container, Divider, Typography } from "@mui/material";
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';

export function EmailPage() {
  const [company, setCompany] = useState('');
  const [loading, setLoading] = useState(false);

  const [competitor, setCompetitor] = useState(null);


  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    setCompetitor(null);

    axios.post('http://localhost:8000/email/', { company })
      .then((response) => {
        console.log('1st then: ', response.data);
        setCompetitor(response.data);

        setLoading(false);
      })
      .catch((error) => {
        setCompany('An error occurred while creating the item.');
        console.error(error);
      });
  };
  return (
    <Container>
      <div style={{ display: "flex", justifyContent: "center" }}>

        <div style={{ display: "flex", flexDirection: "column", width: "50%" }}>
          <h1>Market Competitor Analysis</h1>

          <div style={{ display: "flex", flexDirection: "column", alignItems: "center" }}>
            <TextField
              id="outlined-basic"
              value={company}
              placeholder="Enter Company Name"
              label="Outlined"
              variant="outlined"
              onChange={(e) => setCompany(e.target.value)}
              style={{ width: "100%" }}
            />

            <div style={{ height: "100%" }}>
              {
                loading ?
                  <CircularProgress
                    color="secondary"
                    style={{ margin: "20px" }}
                  /> :
                  <Button
                    variant="contained"
                    onClick={handleSubmit}
                    style={{ margin: "20px" }}
                  >
                    Submit
                  </Button>
              }
            </div>
          </div>
          <Divider />
          <ResponseComponent response={competitor} />
        </div>
      </div>
    </Container>
  );
}

function ResponseComponent({ response }) {

  return (
    <div>
      {
        response !== null ?
          <div>
            <h1> Email: {response.em}</h1>
            <React.Fragment>
              <CardContent>
                <Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>
                  {response.email}
                </Typography>
                <Typography variant="h5" component="div">
                  {response.name}
                </Typography>
                <Typography sx={{ mb: 1.5 }} color="text.secondary">
                  {response.subject}
                </Typography>
                <Typography variant="body2">
                  {response.email_content}
                  <br />
                  {'"a benevolent smile"'}
                </Typography>
              </CardContent>
              <CardActions>
                <Button size="small">Learn More</Button>
              </CardActions>
            </React.Fragment>
          </div>
          :
          <></>
      }
    </div>
  );
}
