import React from "react";
import { BrowserRouter as Router, Route, Link, Routes } from "react-router-dom";
import { MarketGPT } from "./Components/MarketGPT";
import { SocialPage } from "./Components/SocialPage";
import { EmailPage } from "./Components/EmailPage";
import { Box } from "@mui/material";
import AppBar from '@mui/material/AppBar';
import CssBaseline from '@mui/material/CssBaseline';
import Divider from '@mui/material/Divider';
import Drawer from '@mui/material/Drawer';
import IconButton from '@mui/material/IconButton';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import MenuIcon from '@mui/icons-material/Menu';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';

const drawerWidth = 240;
const navItems = ['MarketGPT', 'Social Media Post', 'Email Page'];

function App() {
  return (
    <div><AppBar component="nav">
      <Toolbar>
        <IconButton
          color="inherit"
          aria-label="open drawer"
          edge="start"
          onClick={handleDrawerToggle}
          sx={{ mr: 2, display: { sm: 'none' } }}
        >
          <MenuIcon />
        </IconButton>
        <Typography
          variant="h6"
          component="div"
          sx={{ flexGrow: 1, display: { xs: 'none', sm: 'block' } }}
        >
          MUI
        </Typography>
        <Box sx={{ display: { xs: 'none', sm: 'block' } }}>
          {navItems.map((item) => (
            <Button key={item} sx={{ color: '#fff' }}>
              {item}
            </Button>
          ))}
        </Box>
      </Toolbar>
    </AppBar>
      <Router>
        <ul>
          <li>
            <Link to="/">MarketGPT</Link>
          </li>
          <li>
            <Link to="/profile">Social Media Post</Link>
          </li>
          <li>
            <Link to="/feed">Email Page</Link>
          </li>
        </ul>
        <Routes>
          <Route path="/" element={
            <Box sx={{ bgcolor: '#cfe8fc', height: '100vh' }} >
              <MarketGPT />
            </Box>
          } />
          <Route path="/profile" element={<SocialPage />} />
          <Route path="/feed" element={<EmailPage />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
