import React from "react";
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import { useState } from 'react';
import axios from 'axios';
import { CircularProgress } from "@mui/material";



export function MarketGPT() {
  const [company, setCompany] = useState('');
  const [loading, setLoading] = useState(false);

  const [competitor, setCompetitor] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:8000/market/', { company })
      .then((response) => {
        console.log('1st then: ', response.data);
        setCompetitor(response.data);
      })
      .catch((error) => {
        setCompany('An error occurred while creating the item.');
        console.error(error);
      });
  };
  return (
    <div>
      <h1>Market Competitor Analysis</h1>
      <TextField id="outlined-basic" value = {company}  placeholder="Enter Company Name" label="Outlined" variant="outlined" onChange={(e) => setCompany(e.target.value)}/>
      {
        loading ?
        <CircularProgress color="secondary" /> :
        <Button variant="contained" onClick={handleSubmit}>Submit</Button>
      }
      <p>{JSON.stringify(competitor)}</p>
    </div>
  );
}
